import React from "react";
import {makeStyles} from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import {Divider, Typography} from "@material-ui/core";
import CardMedia from "@material-ui/core/CardMedia";

const useStyles = makeStyles((theme) => ({
    mainGrid: {
        boxShadow: '0 2px 8px 0 rgba(0,0,0,.05)',
        backgroundColor: '#fff',
    },
    post: {
        padding: '0 110px 40px',
    },
    header: {
        padding: '30px 0 10px',
    },
    headerMetaData: {
        marginBottom: '20px',
        "& > span": {
            color: '#a1a1a6',
            borderRight: '1px solid #e0e0e',
            paddingRight: '10px',
            marginRight: '10px',
            fontSize: '14px',
            lineHeight: '18px',
        }
    },
    description: {
        margin: '20px 0',
    },
    picture: {
        margin: '20px 0',
    },
}));


export default function Main(props) {
    const classes = useStyles();
    const {post} = props;

    return (
        <Grid item xs={12} md={9}>
            <div className={classes.mainGrid}>
                <Typography variant="h6" gutterBottom>
                    {post.title}
                </Typography>
                <Paper square={true} elevation={0} className={classes.post}>
                    <div className={classes.header}>
                        <div className={classes.headerMetaData}>
                            <span>{post.postTime}</span>
                            <span>{post.img_urls.length} pictures</span>
                        </div>
                        <Divider/>
                    </div>
                    <Typography className={classes.description}>
                        {post.description}
                    </Typography>
                    {
                        post.imgUrls.map((img) => (
                            <CardMedia
                                image={img}
                                component="img"
                                classes={{
                                    img: classes.picture,
                                }}
                            />
                        ))
                    }
                </Paper>
            </div>
        </Grid>
    )
}
